# -*- coding: utf-8 -*-"""Created on Jun 2 2021@author: Alex"""import numpy as npfrom pyro.dynamic import tranferfunctionfrom pyro.dynamic import statespacefrom pyro.dynamic import massspringdamperfrom pyro.control import linear# Plantm = 1k = 2b = 2plant = massspringdamper.SingleMass(m,k,b)tf    = tranferfunction.ss2tf( plant, 0, 0)tf.bode_plot()tf.pz_map()#Controllerkp  = 10ki  = 3kd  = 0tau = 0.1pid = linear.PIDController(kp,ki,kp,tau)pid.sat = 1000000pid.rbar[0] = 1 # set pointclosed_loop_sys = pid + plantcl_ss = statespace.linearize( closed_loop_sys )cl_tf = tranferfunction.ss2tf( cl_ss, 0, 0)cl_tf.bode_plot()cl_tf.pz_map()closed_loop_sys.plot_trajectory('y')closed_loop_sys.plot_trajectory_with_internal_states('xu')